mapper.select:
SELECT * FROM MEMBER WHERE MEMBER_EMAIL = #{memberEmail}


	AND MEMBER_NAME = #{memberName}



	AND MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBER
MEMBER_NAME = #{memberName}, MEMBER_PASSWORD = #{memberPassword}, MEMBER_EMAIL = #{memberEmail}, MEMBER_IMAGE = #{memberImage}, MEMBER_CHECK = #{memberCheck}, MEMBER_TEL = #{memberTel}
WHERE MEMBER_ID = #{memeberId}
