mapper.select:
SELECT * FROM SPACE
   where space_city=#{spaceCity}
   where space_type=#{spaceType}
   where space_name like '%' || #{spaceName}|| '%'

mapper.update:
UPDATE SPACE
SPACE_NAME=#{spaceName}, SPACE_PRICE=#{spacePrice}, SPACE_CITY=#{spaceCity}, SPACE_ADDRESS=#{spaceAddress}, SPACE_IMAGE_MAIN=#{spaceImageMain}, SPACE_CONTENT=#{spaceContent}, SPACE_STAR=(SELECT AVG(REPLY_STAR) FROM REPLY WHERE SPACE_ID=#{spaceId}), SPACE_IMAGE_SUB1=#{spaceImageSub1}, SPACE_IMAGE_SUB2=#{spaceImageSub2}, SPACE_TYPE=#{spaceType}
WHERE SPACE_ID=#{spaceId}
